/**
 * @file yapp_ym2fil.c
 * Reads metadata from a .ym file, and writes it into a binary file.
 *
 * @verbatim
 * Usage: yapp_ym2fil [options] <metadata-file>
 *     -h  --help                           Display this usage information
 *     -v  --version                        Display the version @endverbatim
 *
 * @author Jayanth Chennamangalam
 * @date 2015.07.05
 */

#include "yapp.h"
#include "yapp_sigproc.h"   /* for SIGPROC filterbank file format support */

/**
 * The build version string, maintained in the file version.c, which is
 * generated by makever.c.
 */
extern const char *g_pcVersion;

int main(int argc, char *argv[])
{
    char *pcFileYM = NULL;
    int iFormat = DEF_FORMAT;
    int iDataFormat = 0;
    int iRet = YAPP_RET_SUCCESS;
    YUM_t stYUM = {{0}};
    char *pcFileOut = NULL;
    char acFileOut[LEN_GENSTRING] = {0};
    const char *pcProgName = NULL;
    int iNextOpt = 0;
    /* valid short options */
    const char* const pcOptsShort = "hvf:";
    /* valid long options */
    const struct option stOptsLong[] = {
        { "help",                   0, NULL, 'h' },
        { "version",                0, NULL, 'v' },
        { NULL,                     0, NULL, 0   }
    };

    /* get the filename of the program from the argument list */
    pcProgName = argv[0];

    /* parse the input */
    do
    {
        iNextOpt = getopt_long(argc, argv, pcOptsShort, stOptsLong, NULL);
        switch (iNextOpt)
        {
            case 'h':   /* -h or --help */
                /* print usage info and terminate */
                PrintUsage(pcProgName);
                return YAPP_RET_SUCCESS;

            case 'v':   /* -v or --version */
                /* display the version */
                (void) printf("%s\n", g_pcVersion);
                return YAPP_RET_SUCCESS;

            case '?':   /* user specified an invalid option */
                /* print usage info and terminate with error */
                (void) fprintf(stderr, "ERROR: Invalid option!\n");
                PrintUsage(pcProgName);
                return YAPP_RET_ERROR;

            case -1:    /* done with options */
                break;

            default:    /* unexpected */
                assert(0);
        }
    } while (iNextOpt != -1);

    /* no arguments */
    if (argc <= optind)
    {
        (void) fprintf(stderr, "ERROR: Input file not specified!\n");
        PrintUsage(pcProgName);
        return YAPP_RET_ERROR;
    }

    /* get the input filename */
    pcFileYM = argv[optind];

    /* determine the file type */
    iFormat = YAPP_GetFileType(pcFileYM);
    if (YAPP_RET_ERROR == iFormat)
    {
        (void) fprintf(stderr,
                       "ERROR: File type determination failed!\n");
        return YAPP_RET_ERROR;
    }
    if (iFormat != YAPP_FORMAT_YM)
    {
        (void) fprintf(stderr,
                       "ERROR: Invalid file type!\n");
        return YAPP_RET_ERROR;
    }

    iRet = YAPP_ReadSIGPROCHeaderFile(pcFileYM, &stYUM);
    if (iRet != YAPP_RET_SUCCESS)
    {
        (void) fprintf(stderr, "ERROR: Reading metadata failed!\n");
        YAPP_CleanUp();
        return YAPP_RET_ERROR;
    }

    /* open the time series data file for writing */
    pcFileOut = YAPP_GetFilenameFromPath(pcFileYM);
    if (NULL == pcFileOut) {
        (void) fprintf(stderr, "ERROR: Getting filename failed!\n");
        YAPP_CleanUp();
        return YAPP_RET_ERROR;
    }

    (void) sprintf(acFileOut,
                   "%s.%s%s",
                   pcFileOut,
                   INFIX_HEADER,
                   EXT_FIL);

    /* write out the filterbank-format header */
    iRet = YAPP_WriteMetadata(acFileOut, YAPP_FORMAT_FIL, stYUM);
    if (iRet != YAPP_RET_SUCCESS)
    {
        (void) fprintf(stderr, "ERROR: Writing metadata failed!\n");
        YAPP_CleanUp();
        return YAPP_RET_ERROR;
    }

    /* clean up */
    YAPP_CleanUp();

    return YAPP_RET_SUCCESS;
}

/*
 * Prints usage information
 */
void PrintUsage(const char *pcProgName)
{
    (void) printf("Usage: %s [options] <metadata-file>\n",
                  pcProgName);
    (void) printf("    -h  --help                          ");
    (void) printf("Display this usage information\n");
    (void) printf("    -v  --version                       ");
    (void) printf("Display the version\n");

    return;
}

